import React, { useState } from "react";
import "./ProgressBar.css";
const ProgressBar = () => {
  const [currentStep, setCurrentStep] = useState(1);

  const handleNext = () => {
    if (currentStep < 4) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  return (
    <div className="progress-bar-container">
      <div className="progress-bar">
        <div className="step">Step {currentStep}</div>
        <div className="functionality">
          {currentStep === 1 && (
            <div className="step-functionality">
              <p>
                Miners are currently mining the block. This step involves
                complex calculations to secure the blockchain network.
              </p>
              <img
                src="data:image/jpeg;base64,"
                className="step-image"
              />
            </div>
          )}
          {currentStep === 2 && (
            <div className="step-functionality">
              <p>
                Stakeholders are putting their stake on the recently mined
                block. This helps in reaching consensus and validating
                transactions.
              </p>
              <img
                src="data:image/jpeg;base64,"
                className="step-image"
              />
            </div>
          )}
          {currentStep === 3 && (
            <div className="step-functionality">
              <p>
                The results of the transactions are being processed and verified
                to ensure accuracy and integrity in the blockchain network.
              </p>
              <img
                src="https://i.gifer.com/origin/a1/a1d81f564eeb1468aefbcfd54d1571b8_w200.gif"
                className="step-image"
              />
            </div>
          )}
          {currentStep === 4 && (
            <div className="step-functionality">
              <p>
                The results have been processed, and now the system is
                determining whether the participant should be rewarded based on
                their actions and contributions to the network.
              </p>
              <img
                src="data:image/jpeg;base64,"
                className="step-image"
              />
            </div>
          )}
        </div>
        <div className="buttons">
          <button onClick={handlePrevious} disabled={currentStep === 1}>
            Previous
          </button>
          <button onClick={handleNext} disabled={currentStep === 4}>
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProgressBar;
